import react, { Fragment, useEffect, useState } from 'react';
import './Homepage.css'

export default function Homepage({ lightBg, horizontalAlign, topline, headline, description, img, altText }) {

    return (
        <div className={"homepage-container " + (lightBg ? "light-Display" : "dark-Display")}>
            <div className="container">

                {
                horizontalAlign ? 
                    <div className="row home-row">
                        <div className="col">

                            {topline}

                        </div>
                        <div className="col">
                            {headline}
                        </div>
                    </div>
                :
                <>
                <div className="row">
                    {topline}
                </div>
                <div className="row">
                    {description}
                </div>
                </>
                }
            </div>
        </div>
    );
}
